import React, { useState, useEffect, useRef } from 'react';

function SlidingImages() {
    const images = [
        "/icons/events&activities/e&a1.jpg",
        "/icons/events&activities/e&a2.jpg",
        "/icons/events&activities/e&a3.jpg",
        "/icons/events&activities/e&a4.jpg",
        "/icons/events&activities/e&a5.jpg",
        "/icons/events&activities/e&a6.jpg",
        "/icons/events&activities/e&a7.jpg",
        "/icons/events&activities/e&a8.jpg",
        "/icons/events&activities/e&a9.jpg",
        "/icons/events&activities/e&a10.jpg"
    ];

    const [index, setIndex] = useState(0);
    const imageRef = useRef(null);

    useEffect(() => {
        const intervalId = setInterval(() => {
            const nextIndex = (index + 1) % images.length;
            setIndex(nextIndex);
            imageRef.current.style.transform = `translateX(-${nextIndex * 100}%)`;
        }, 5000);

        return () => clearInterval(intervalId);
    }, [index, images.length]);

    return (
        <div className="sliding-images">
            <div className="image-group" ref={imageRef}>
                {images.map((image, i) => (
                    <img
                        key={i}
                        src={image}
                        alt={`Image ${i + 1}`}
                    />
                ))}
            </div>
        </div>
    );
}

export default SlidingImages;
