    window.location.href = "/campus#adF";
    const moreSection = document.getElementById("adF");
    if (moreSection) {
      moreSection.scrollIntoView({ behavior: "smooth", block: "start" });
    }
  };

  const scrollToWhy = () => {
    window.location.href = "/campus#whyUs";
